mapper.select:
SELECT *
FROM review
WHERE youtube_id = #{youtubeId} AND id = #{id}

mapper.update:
UPDATE review
SET title = #{title}, content = #{content}
WHERE youtube_id = #{youtubeId}
AND writer = (SELECT nickname FROM user WHERE id = #{presentid})

UPDATE review
SET view_cnt = view_cnt + 1
WHERE youtube_id = #{youtubeId} AND id = #{id}
